// With no item id; Shows an grass block
ACHIEVEMENTGET("Hello Grass")

// With an item id
ACHIEVEMENTGET("Hello Stone","stone")

// With an item id and metadata
ACHIEVEMENTGET("Hello Birch","log:2")

// Iterate over all controls everywhere
FOREACH(controls)

// Iterate over all controls in the layout "ingame"
FOREACH(controls(ingame))

// Iterate over all button controls
FOREACH(controls(:button))

// Iterate over all button controls in "ingame"
FOREACH(controls(ingame:button))

// Example usage, set all labels to blue
FOREACH(controls(default:label));
   SETPROPERTY(%CONTROLNAME%,"colour","blue");
NEXT;

// Crafts only once
CRAFT("planks")

// Crafts 12 planks
CRAFT("planks",12)

// Crafts 12 planks and throws it on the ground
CRAFT("planks",12,true)

// Logs additional messages to the chat window
CRAFT("planks",12,false,true)

// Crafts only once
CRAFTANDWAIT("planks")

// Crafts 12 planks
CRAFTANDWAIT("planks",12)

// Crafts 12 planks and throws it on the ground
CRAFTANDWAIT("planks",12,true)

// Logs additional messages to the chat window
CRAFTANDWAIT("planks",12,false,true)

// Retrieves information about the block below the player
GETIDREL(0,-1,0,&variant1)
&variant2 = GETIDREL(0,-1,0,,#data)

IFBEGINSWITH("Hello World Example", "Hello")
	// "Hello World Example" starts with "Hello"
ENDIF

IFCONTAINS("Hello World Example", "World")
	// "World" is in "Hello World Example"
ENDIF

IFENDSWITH("Hello World Example", "Example")
	// "Hello World Example" ends with "Example"
ENDIF

&input = "Hello World!!"
&regex = "(Hello) (World)"

MATCH(%&input%,%&regex%,&match)
// &match now contains the whole match "Hello World"

MATCH(%&input%,%&regex%,&match,2)
// &match now contains only the second group "World"

MATCH("no match",%&regex%,&match,1,"default")
// &match now contains the default value "default", since the regex couldn't match the input

MATCH(%&input%,%&regex%,{&hello,&world})
// &hello and &world now contain the first and second group

&groups[] = MATCH(%&input%,%&regex%)
// &groups[] now contains the whole match and the two groups

// Places an empty sign
PLACESIGN()

// Places a sign with only one line
PLACESIGN("Hello Sign")

// Places a sign with all four lines filled in
PLACESIGN("Line 1","Line 2","Line 3","Line 4")

// Places a sign with two filled lines but leaves the GUI open
// so that you can continue to edit the sign text
PLACESIGN("Hello","World",,,true)

PLAYSOUND("entity.creeper.primed")

// Has to be in .ogg format
PLAYSOUND("custom.sound")

// Adds an element to the end of the array
PUSH(&array[],"Hello")

// The same as
&array[] = "World"

// Outputs "Hello" and "World"
FOREACH(&array[],&string)
    LOG(%&string%)
NEXT

#variant1 = RANDOM()
RANDOM(#variant2)
// By default generate a random integer between 0 and 100

RANDOM(,1)
// Either 0 or 1

RANDOM(,5,10)
// Between 5 and 10

RANDOM(,10,5)
// Also between 5 and 10 (Ordering is not important)

&string = "Looking for Hello World"

// Deletes "for" from &string
REPLACE(&string,"for")
// Ouputs "Looking  Hello World"
LOG(%&string%)

// Replaces "Hello" with the given string
REPLACE(&string,"Hello","Bye")
// Outputs "Looking  Bye World"
LOG(%&string%)

// Doesn't modify &string
&result = REPLACE(&string,"World","Level")
// Outputs "Looking  Bye Level" and "Looking  Bye World" respectively
LOG(%&result%)
LOG(%&string%)
